import React, { useEffect, useState } from "react";

import * as S from "./styles";

const Main = ({ title = "WPM GOOOOO" }) => {
  const [count, setCount] = useState(60);
  const [playing, setPlaying] = useState(false);

  useEffect(() => {
    const countdown = setInterval(() => {
      setCount((count) => (count > 0 ? count - 1 : 60));
    }, 1000);

    return () => {
      clearInterval(countdown);
    };
  }, [playing]);

  const words = ["Arrow", "Tomato", "Star", "Wheel"];

  const shuffleArray = (arr: Array<string>) =>
    arr
      .map((a) => [Math.random(), a])
      .sort()
      .map((a) => a[1]);

  return (
    <S.Container>
      <S.Button onClick={() => setPlaying(true)}>Iniciar</S.Button>
      <S.Title>CONTAGEM REGRESSIVA: {count}</S.Title>

      <S.TextBox type="text" onFocus={() => setPlaying(true)} />

      {shuffleArray(words).map((word) => (
        <p>{word}</p>
      ))}
    </S.Container>
  );
};

export default Main;
