import React, { useEffect, useState } from "react";

import * as S from "./styles";

const Main = ({ title = "WPM GOOOOO" }) => {
  const [count, setCount] = useState(60);

  useEffect(() => {
    setInterval(() => {
      setCount((count) => {
        if (count > 0) {
          return count - 1;
        } else {
          return 60;
        }
      });
    }, 1000);
  }, []);

  return (
    <S.Container>
      <S.Title>CONTAGEM REGRESSIVA: {count}</S.Title>

      <div></div>
    </S.Container>
  );
};

export default Main;
