import React, { useEffect, useState, useCallback } from "react";

import * as S from "./styles";

const Main = ({ title = "WPM GOOOOO" }) => {
  const [count, setCount] = useState(60);
  const [playing, setPlaying] = useState(false);
  const [words, setWords] = useState(["Arrow", "Tomato", "Star", "Wheel"]);

  useEffect(() => {
    if (playing) {
      const countdown = setInterval(() => {
        setCount((count) => (count > 0 ? count - 1 : 60));
      }, 1000);

      return () => {
        clearInterval(countdown);
      };
    }
  }, [playing]);

  const shuffleArray = (arr: Array<string>) =>
    arr
      .map((a) => [Math.random(), a])
      .sort()
      .map((a) => a[1]);

  const shuffledArray = useCallback(() => {
    return shuffleArray(words);
  }, [shuffleArray, words]);

  return (
    <S.Container>
      <S.Title>CONTAGEM REGRESSIVA: {count}</S.Title>

      <S.TextBox type="text" onFocus={() => setPlaying(true)} />

      {shuffledArray().map((word) => (
        <p>{word}</p>
      ))}
    </S.Container>
  );
};

export default Main;
