import React, { useEffect, useState } from "react";

import * as S from "./styles";

const Main = ({ title = "WPM GOOOOO" }) => {
  const [count, setCount] = useState(60);

  useEffect(() => {
    setInterval(() => {
      setCount((state) => {
        if (state > 0) state - 1;
      });
    }, 1000);
  }, []);

  return (
    <S.Container>
      <S.Title>{title}</S.Title>
    </S.Container>
  );
};

export default Main;
